from langflow.base.io.text import TextComponent
from langflow.io import MultilineInput, Output
from langflow.schema.message import Message

class AbstractInput(TextComponent):
    display_name = "Abstract Input"
    description = "Provide the abstract text."
    icon = "type"
    name = "AbstractInput"

    inputs = [
        MultilineInput(
            name="abstract_text",
            display_name="Abstract Text",
            info="Paste the abstract here."
        )
    ]

    outputs = [
        Output(display_name="Abstract Output", name="text", method="text_response"),
    ]

    def text_response(self) -> Message:
        return Message(text=self.abstract_text)

